import React, { useEffect, useState } from "react";
import axios from "axios";
import "./styles.css";

// Función para cargar preguntas de acuerdo al idioma seleccionado
const loadQuestionsByLanguage = async (language) => {
  let response;
  try {
    response = await axios.get(
      `http://localhost:3001/questions-${language}.json`
    );
    return response.data;
  } catch (error) {
    console.error(`Error fetching ${language} questions:`, error);
    return [];
  }
};

const shuffleArray = (array) => {
  let currentIndex = array.length;
  let temporaryValue, randomIndex;

  while (currentIndex !== 0) {
    randomIndex = Math.floor(Math.random() * currentIndex);
    currentIndex--;

    temporaryValue = array[currentIndex];
    array[currentIndex] = array[randomIndex];
    array[randomIndex] = temporaryValue;
  }

  return array;
};

const App = () => {
  const [questions, setQuestions] = useState([]);
  const [selectedQuestions, setSelectedQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState("");
  const [score, setScore] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [timeLeft, setTimeLeft] = useState(60);
  const [answerMessage, setAnswerMessage] = useState("");
  const [numQuestions, setNumQuestions] = useState(5);
  const [showCorrectAnswer, setShowCorrectAnswer] = useState(false);
  const [language, setLanguage] = useState("es"); // Estado para el idioma seleccionado

  useEffect(() => {
    const fetchQuestions = async () => {
      const fetchedQuestions = await loadQuestionsByLanguage(language);
      if (fetchedQuestions.length > 0) {
        const shuffledQuestions = shuffleArray(fetchedQuestions);
        setQuestions(shuffledQuestions);
      }
    };
    fetchQuestions();
  }, [language]);

  useEffect(() => {
    if (timeLeft === 0) {
      handleNextQuestion();
    }
    const timer = setInterval(() => {
      setTimeLeft((prevTime) => prevTime - 1);
    }, 1000);

    return () => clearInterval(timer);
  }, [timeLeft]);

  const handleOptionChange = (e) => {
    setSelectedOption(e.target.value);
    setShowCorrectAnswer(false);
  };

  const checkAnswer = () => {
    if (selectedOption === selectedQuestions[currentQuestionIndex]?.answer) {
      setScore((prevScore) => prevScore + 1);
      setAnswerMessage("¡Respuesta Correcta!");
    } else {
      setAnswerMessage("Respuesta Incorrecta");
    }
    setShowCorrectAnswer(true);
  };

  const handleNextQuestion = () => {
    setAnswerMessage("");
    setShowCorrectAnswer(false);

    if (currentQuestionIndex < selectedQuestions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      setShowScore(true);
    }

    setSelectedOption("");
    setTimeLeft(60);
  };

  const handleResetQuiz = () => {
    window.location.reload();
  };

  const handleNumQuestionsChange = (e) => {
    setNumQuestions(Number(e.target.value));
  };

  const startQuiz = () => {
    const shuffledQuestions = shuffleArray(questions);
    const selected = shuffledQuestions.slice(0, numQuestions);
    setSelectedQuestions(selected);
    setShowScore(false);
    setCurrentQuestionIndex(0);
    setScore(0);
    setTimeLeft(60);
  };

  const changeLanguage = (lang) => {
    setLanguage(lang);
    setQuestions([]); // Limpia las preguntas actuales para cargar nuevas preguntas del idioma seleccionado
  };

  return (
    <div className="App">
      <div className="container">
        <div className="language-buttons">
          <button onClick={() => changeLanguage("es")}>ES</button>
          <button onClick={() => changeLanguage("en")}>EN</button>
          <button onClick={() => changeLanguage("de")}>DE</button>
        </div>
        {showScore ? (
          <div>
            <p>
              Tu puntuación es {score} de {selectedQuestions.length}
            </p>
            <p>
              Porcentaje de acierto:
              {((score / selectedQuestions.length) * 100).toFixed(2)}%
            </p>
            <div className="button-container">
              <button onClick={handleResetQuiz}>Reiniciar Quiz</button>
            </div>
          </div>
        ) : (
          <div>
            {selectedQuestions.length === 0 ? (
              <div>
                <label>
                  Elegir cantidad de preguntas:
                  <input
                    type="number"
                    min="1"
                    max={questions.length}
                    value={numQuestions}
                    onChange={handleNumQuestionsChange}
                  />
                </label>
                <button onClick={startQuiz}>Iniciar Quiz</button>
              </div>
            ) : (
              <div>
                <div className="timer">Tiempo restante: {timeLeft}s</div>
                <h2>{selectedQuestions[currentQuestionIndex].question}</h2>
                <div>
                  {selectedQuestions[currentQuestionIndex].options.map(
                    (option, index) => (
                      <div
                        key={index}
                        className={`option ${
                          showCorrectAnswer &&
                          option ===
                            selectedQuestions[currentQuestionIndex].answer
                            ? "correct"
                            : showCorrectAnswer && option === selectedOption
                            ? "incorrect"
                            : ""
                        }`}
                      >
                        <input
                          type="radio"
                          name="option"
                          value={option}
                          checked={selectedOption === option}
                          onChange={handleOptionChange}
                          disabled={showCorrectAnswer}
                        />
                        {option}
                      </div>
                    )
                  )}
                </div>
                <div className="button-container">
                  <button onClick={checkAnswer} disabled={showCorrectAnswer}>
                    Verificar Respuesta
                  </button>
                  {answerMessage && <p>{answerMessage}</p>}
                  <button onClick={handleNextQuestion}>
                    Siguiente Pregunta
                  </button>
                </div>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
