import React, { useEffect, useState } from "react";
import axios from "axios";
import "./styles.css";

import textosES from "./textos-es.json";
import textosEN from "./textos-en.json";
import textosDE from "./textos-de.json";

import LanguageButtons from "./LanguageButtons";
import StartQuiz from "./StartQuiz";
import Quiz from "./Quiz";
import Score from "./Score";

const shuffleArray = (array) => {
  let currentIndex = array.length;
  let temporaryValue, randomIndex;

  while (currentIndex !== 0) {
    randomIndex = Math.floor(Math.random() * currentIndex);
    currentIndex--;

    temporaryValue = array[currentIndex];
    array[currentIndex] = array[randomIndex];
    array[randomIndex] = temporaryValue;
  }

  return array;
};

const App = () => {
  const [questions, setQuestions] = useState([]);
  const [selectedQuestions, setSelectedQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState("");
  const [score, setScore] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [timeLeft, setTimeLeft] = useState(60);
  const [answerMessage, setAnswerMessage] = useState("");
  const [numQuestions, setNumQuestions] = useState(5);
  const [showCorrectAnswer, setShowCorrectAnswer] = useState(false);
  const [language, setLanguage] = useState("es");
  const [selectedLanguage, setSelectedLanguage] = useState("es");
  const [quizStarted, setQuizStarted] = useState(false);

  useEffect(() => {
    const fetchQuestions = async () => {
      const fetchedQuestions = await loadQuestionsByLanguage(language);
      if (fetchedQuestions.length > 0) {
        const shuffledQuestions = shuffleArray(fetchedQuestions);
        setQuestions(shuffledQuestions);
      }
    };
    fetchQuestions();
  }, [language]);

  useEffect(() => {
    if (timeLeft === 0) {
      handleNextQuestion();
    }
    const timer = setInterval(() => {
      setTimeLeft((prevTime) => prevTime - 1);
    }, 1000);

    return () => clearInterval(timer);
  }, [timeLeft]);

  const loadQuestionsByLanguage = async (lang) => {
    try {
      const response = await axios.get(
        `http://localhost:3001/questions-${lang}.json`
      );
      return response.data;
    } catch (error) {
      console.error("Error fetching questions:", error);
      return [];
    }
  };

  const handleOptionChange = (option) => {
    setSelectedOption(option);
    setShowCorrectAnswer(false);
  };

  const checkAnswer = () => {
    if (selectedOption === selectedQuestions[currentQuestionIndex]?.answer) {
      setScore((prevScore) => prevScore + 1);
      setAnswerMessage(textos[language].respuestaCorrecta);
    } else {
      setAnswerMessage(textos[language].respuestaIncorrecta);
    }
    setShowCorrectAnswer(true);
  };

  const handleNextQuestion = () => {
    if (!showCorrectAnswer && selectedOption) {
      checkAnswer();
    }

    setShowCorrectAnswer(false);
    setAnswerMessage("");

    if (currentQuestionIndex < selectedQuestions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
      setSelectedOption("");
      setTimeLeft(60);
    } else {
      setShowScore(true);
    }
  };

  const handleResetQuiz = () => {
    setShowScore(false);
    setCurrentQuestionIndex(0);
    setScore(0);
    setQuizStarted(false);
    setSelectedQuestions([]);
    setTimeLeft(60);
  };

  const handleNumQuestionsChange = (e) => {
    setNumQuestions(Number(e.target.value));
  };

  const startQuiz = () => {
    if (questions.length === 0) {
      console.error("No questions loaded");
      return;
    }
    setSelectedQuestions(shuffleArray(questions).slice(0, numQuestions));
    setShowScore(false);
    setCurrentQuestionIndex(0);
    setScore(0);
    setTimeLeft(60);
    setQuizStarted(true);
  };

  const changeLanguage = (lang) => {
    setLanguage(lang);
    setSelectedLanguage(lang);
  };

  const textos = {
    es: textosES,
    en: textosEN,
    de: textosDE,
  };

  return (
    <div className="App">
      <LanguageButtons
        selectedLanguage={selectedLanguage}
        changeLanguage={changeLanguage}
      />
      <div className="container">
        {!quizStarted && !showScore && (
          <StartQuiz
            textos={textos[language]}
            numQuestions={numQuestions}
            handleNumQuestionsChange={handleNumQuestionsChange}
            startQuiz={startQuiz}
            questionsLength={questions.length}
          />
        )}
        {quizStarted && !showScore && selectedQuestions.length > 0 && (
          <Quiz
            textos={textos[language]}
            selectedQuestions={selectedQuestions}
            currentQuestionIndex={currentQuestionIndex}
            timeLeft={timeLeft}
            selectedOption={selectedOption}
            showCorrectAnswer={showCorrectAnswer}
            answerMessage={answerMessage}
            handleOptionChange={handleOptionChange}
            checkAnswer={checkAnswer}
            handleNextQuestion={handleNextQuestion}
            score={score}
          />
        )}
        {showScore && (
          <Score
            textos={textos[language]}
            score={score}
            numQuestions={numQuestions}
            handleResetQuiz={handleResetQuiz}
            selectedQuestionsLength={selectedQuestions.length}
          />
        )}
      </div>
    </div>
  );
};

export default App;
