import React, { useEffect, useState } from "react";
import axios from "axios";
import "./styles.css";

// Importa los archivos JSON de textos para los idiomas
import textosEs from "./textos-es.json";
import textosEn from "./textos-en.json";
import textosDe from "./textos-de.json";

const shuffleArray = (array) => {
  let currentIndex = array.length;
  let temporaryValue, randomIndex;

  while (currentIndex !== 0) {
    randomIndex = Math.floor(Math.random() * currentIndex);
    currentIndex--;

    temporaryValue = array[currentIndex];
    array[currentIndex] = array[randomIndex];
    array[randomIndex] = temporaryValue;
  }

  return array;
};

const App = () => {
  const [questions, setQuestions] = useState([]);
  const [selectedQuestions, setSelectedQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState("");
  const [score, setScore] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [timeLeft, setTimeLeft] = useState(60);
  const [answerMessage, setAnswerMessage] = useState("");
  const [numQuestions, setNumQuestions] = useState(5);
  const [showCorrectAnswer, setShowCorrectAnswer] = useState(false);
  const [language, setLanguage] = useState("es"); // Estado para el idioma actual

  // Textos est치ticos en diferentes idiomas
  const idiomaTextos = {
    es: textosEs,
    en: textosEn,
    de: textosDe,
  };

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await axios.get(
          `http://localhost:3001/questions-${language}`
        );
        const shuffledQuestions = shuffleArray(response.data);
        setQuestions(shuffledQuestions);
      } catch (error) {
        console.error("Error fetching questions:", error);
      }
    };
    fetchQuestions();
  }, [language]);

  useEffect(() => {
    if (timeLeft === 0) {
      handleNextQuestion();
    }
    const timer = setInterval(() => {
      setTimeLeft((prevTime) => prevTime - 1);
    }, 1000);

    return () => clearInterval(timer);
  }, [timeLeft]);

  const handleOptionChange = (e) => {
    setSelectedOption(e.target.value);
    setShowCorrectAnswer(false);
  };

  const checkAnswer = () => {
    if (selectedOption === selectedQuestions[currentQuestionIndex]?.answer) {
      setScore((prevScore) => prevScore + 1);
      setAnswerMessage(idiomaTextos[language].respuestaCorrecta);
    } else {
      setAnswerMessage(idiomaTextos[language].respuestaIncorrecta);
    }
    setShowCorrectAnswer(true);
  };

  const handleNextQuestion = () => {
    setAnswerMessage("");
    setShowCorrectAnswer(false);

    if (currentQuestionIndex < selectedQuestions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      setShowScore(true);
    }

    // Contabilizar la respuesta si es correcta o incorrecta al pasar a la siguiente pregunta
    if (selectedOption === selectedQuestions[currentQuestionIndex]?.answer) {
      setScore((prevScore) => prevScore + 1);
    }

    setSelectedOption("");
    setTimeLeft(60);
  };

  const handleResetQuiz = () => {
    window.location.reload();
  };

  const handleNumQuestionsChange = (e) => {
    setNumQuestions(Number(e.target.value));
  };

  const handleLanguageChange = (lang) => {
    setLanguage(lang);
    setCurrentQuestionIndex(0); // Reiniciar al cambiar de idioma
    setScore(0); // Reiniciar la puntuaci칩n al cambiar de idioma
    setShowScore(false); // Ocultar el puntaje al cambiar de idioma
  };

  const startQuiz = () => {
    const shuffledQuestions = shuffleArray(questions);
    const selected = shuffledQuestions.slice(0, numQuestions);
    setSelectedQuestions(selected);
    setShowScore(false);
    setCurrentQuestionIndex(0);
    setScore(0);
    setTimeLeft(60);
  };

  return (
    <div className="App">
      <div className="container">
        {showScore ? (
          <div>
            <p>
              {idiomaTextos[language].puntuacion}: {score} /{" "}
              {selectedQuestions.length}
            </p>
            <p>
              {idiomaTextos[language].porcentajeEficacia}:{" "}
              {((score / selectedQuestions.length) * 100).toFixed(2)}%
            </p>
            <div className="button-container">
              <button onClick={handleResetQuiz}>
                {idiomaTextos[language].reiniciarQuiz}
              </button>
            </div>
          </div>
        ) : (
          <div>
            {selectedQuestions.length === 0 ? (
              <div>
                <label>
                  {idiomaTextos[language].elegirCantidadPreguntas}
                  <input
                    type="number"
                    min="1"
                    max={questions.length}
                    value={numQuestions}
                    onChange={handleNumQuestionsChange}
                  />
                </label>
                <button onClick={startQuiz}>
                  {idiomaTextos[language].iniciarQuiz}
                </button>
              </div>
            ) : (
              <div>
                <div className="timer">
                  {idiomaTextos[language].tiempoRestante}: {timeLeft}s
                </div>
                <h2>{selectedQuestions[currentQuestionIndex].question}</h2>
                <div>
                  {selectedQuestions[currentQuestionIndex].options.map(
                    (option, index) => (
                      <div
                        key={index}
                        className={`option ${
                          showCorrectAnswer &&
                          option ===
                            selectedQuestions[currentQuestionIndex].answer
                            ? "correct"
                            : showCorrectAnswer && option === selectedOption
                            ? "incorrect"
                            : ""
                        }`}
                      >
                        <input
                          type="radio"
                          name="option"
                          value={option}
                          checked={selectedOption === option}
                          onChange={handleOptionChange}
                          disabled={showCorrectAnswer}
                        />
                        {option}
                      </div>
                    )
                  )}
                </div>
                <div className="button-container">
                  <button onClick={checkAnswer} disabled={showCorrectAnswer}>
                    {idiomaTextos[language].verificarRespuesta}
                  </button>
                  {answerMessage && <p>{answerMessage}</p>}
                  <button onClick={handleNextQuestion}>
                    {idiomaTextos[language].siguientePregunta}
                  </button>
                </div>
              </div>
            )}
          </div>
        )}
        {/* Botones para cambiar de idioma */}
        <div className="language-buttons">
          <button
            onClick={() => handleLanguageChange("es")}
            className={language === "es" ? "active" : ""}
          >
            ES
          </button>
          <button
            onClick={() => handleLanguageChange("en")}
            className={language === "en" ? "active" : ""}
          >
            EN
          </button>
          <button
            onClick={() => handleLanguageChange("de")}
            className={language === "de" ? "active" : ""}
          >
            DE
          </button>
        </div>
      </div>
    </div>
  );
};

export default App;
