import React, { useEffect, useState } from "react";
import axios from "axios";
import { BrowserRouter as Router } from "react-router-dom";
import "./styles.css";

const shuffleArray = (array) => {
  let currentIndex = array.length;
  let temporaryValue, randomIndex;

  while (currentIndex !== 0) {
    randomIndex = Math.floor(Math.random() * currentIndex);
    currentIndex--;

    temporaryValue = array[currentIndex];
    array[currentIndex] = array[randomIndex];
    array[randomIndex] = temporaryValue;
  }

  return array;
};

const App = () => {
  const [questions, setQuestions] = useState([]);
  const [selectedQuestions, setSelectedQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState("");
  const [score, setScore] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [timeLeft, setTimeLeft] = useState(60);
  const [answerMessage, setAnswerMessage] = useState("");
  const [numQuestions, setNumQuestions] = useState(5);

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await axios.get("http://localhost:3001/questions");
        const shuffledQuestions = shuffleArray(response.data);
        setQuestions(shuffledQuestions);
      } catch (error) {
        console.error("Error fetching questions:", error);
      }
    };
    fetchQuestions();
  }, []);

  useEffect(() => {
    const timer = setInterval(() => {
      if (timeLeft > 0) {
        setTimeLeft((prevTime) => prevTime - 1);
      } else {
        handleNextQuestion();
      }
    }, 1000);

    return () => clearInterval(timer);
  }, [timeLeft]);

  const handleOptionChange = (e) => {
    setSelectedOption(e.target.value);
  };

  const handleCheckAnswer = () => {
    const correctAnswer = selectedQuestions[currentQuestionIndex]?.answer;
    if (selectedOption === correctAnswer) {
      setScore(score + 1);
      setAnswerMessage("¡Respuesta correcta!");
    } else {
      setAnswerMessage("Respuesta incorrecta.");
    }
  };

  const handleNextQuestion = () => {
    setAnswerMessage("");

    if (currentQuestionIndex < selectedQuestions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      setShowScore(true);
    }

    setSelectedOption("");
    setTimeLeft(60);
  };

  const handleResetQuiz = () => {
    setSelectedQuestions([]);
    setShowScore(false);
    setCurrentQuestionIndex(0);
    setScore(0);
    setTimeLeft(60);
  };

  const handleNumQuestionsChange = (e) => {
    setNumQuestions(Number(e.target.value));
  };

  const startQuiz = () => {
    const shuffledQuestions = shuffleArray(questions);
    const selected = shuffledQuestions.slice(0, numQuestions);
    setSelectedQuestions(selected);
    setShowScore(false);
    setCurrentQuestionIndex(0);
    setScore(0);
    setTimeLeft(60);
  };

  return (
    <Router>
      <div className="App">
        <div className="container">
          {showScore ? (
            <div>
              <p>
                Tu puntaje es {score} de {selectedQuestions.length}
              </p>
              <p>
                Porcentaje de efectividad:{" "}
                {((score / selectedQuestions.length) * 100).toFixed(2)}%
              </p>
              <div className="button-container">
                <button onClick={handleResetQuiz}>Reiniciar Quiz</button>
              </div>
            </div>
          ) : (
            <div>
              {selectedQuestions.length === 0 ? (
                <div>
                  <label>
                    Número de preguntas:
                    <input
                      type="number"
                      min="1"
                      max={questions.length}
                      value={numQuestions}
                      onChange={handleNumQuestionsChange}
                    />
                  </label>
                  <button onClick={startQuiz}>Comenzar Quiz</button>
                </div>
              ) : (
                <div>
                  <div className="timer">Tiempo restante: {timeLeft}s</div>
                  <h2>{selectedQuestions[currentQuestionIndex]?.question}</h2>
                  <div>
                    {selectedQuestions[currentQuestionIndex]?.options.map(
                      (option, index) => (
                        <div key={index}>
                          <input
                            type="radio"
                            id={`option${index}`}
                            name="option"
                            value={option.text} // Asegúrate de que 'text' sea la propiedad correcta de la opción
                            checked={selectedOption === option.text}
                            onChange={handleOptionChange}
                          />
                          <label htmlFor={`option${index}`}>
                            {option.text}
                          </label>
                        </div>
                      )
                    )}
                  </div>
                  <div className="button-container">
                    <button onClick={handleCheckAnswer}>
                      Verificar Respuesta
                    </button>
                    {answerMessage && <p>{answerMessage}</p>}
                    <button onClick={handleNextQuestion}>
                      Siguiente Pregunta
                    </button>
                  </div>
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </Router>
  );
};

export default App;
