import React, { useEffect, useState } from "react";
import axios from "axios";
import "./styles.css";

const shuffleArray = (array) => {
  let currentIndex = array.length;
  let temporaryValue, randomIndex;

  // Mientras queden elementos a mezclar...
  while (currentIndex !== 0) {
    // Seleccionar un elemento sin mezclar de forma aleatoria
    randomIndex = Math.floor(Math.random() * currentIndex);
    currentIndex--;

    // Intercambiarlo con el elemento actual
    temporaryValue = array[currentIndex];
    array[currentIndex] = array[randomIndex];
    array[randomIndex] = temporaryValue;
  }

  return array;
};

const App = () => {
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState("");
  const [score, setScore] = useState(0);
  const [showScore, setShowScore] = useState(false);
  const [timeLeft, setTimeLeft] = useState(60);

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await axios.get("http://localhost:3001/questions");
        const shuffledQuestions = shuffleArray(response.data); // Mezcla aleatoria de las preguntas obtenidas
        setQuestions(shuffledQuestions);
      } catch (error) {
        console.error("Error fetching questions:", error);
      }
    };
    fetchQuestions();
  }, []);

  useEffect(() => {
    if (timeLeft === 0) {
      handleNextQuestion();
    }
    const timer = setInterval(() => {
      setTimeLeft((prevTime) => prevTime - 1);
    }, 1000);

    return () => clearInterval(timer);
  }, [timeLeft]);

  const handleOptionChange = (e) => {
    setSelectedOption(e.target.value);
  };

  const handleNextQuestion = () => {
    if (selectedOption === questions[currentQuestionIndex]?.answer) {
      setScore(score + 1);
    }
    setSelectedOption("");
    setTimeLeft(60);

    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      setShowScore(true);
    }
  };

  const handleResetQuiz = () => {
    const shuffledQuestions = shuffleArray(questions); // Mezcla las preguntas nuevamente al reiniciar
    setQuestions(shuffledQuestions);
    setCurrentQuestionIndex(0);
    setScore(0);
    setShowScore(false);
    setSelectedOption("");
    setTimeLeft(60);
  };

  return (
    <div className="App">
      <div className="container">
        {showScore ? (
          <div>
            Tu puntaje es {score} de {questions.length}
            <div className="button-container">
              <button onClick={handleResetQuiz}>Reiniciar Quiz</button>
            </div>
          </div>
        ) : (
          questions.length > 0 && (
            <div>
              <div className="timer">Tiempo restante: {timeLeft}s</div>
              <h2>{questions[currentQuestionIndex].question}</h2>
              <div>
                {questions[currentQuestionIndex].options.map(
                  (option, index) => (
                    <div key={index}>
                      <input
                        type="radio"
                        name="option"
                        value={option}
                        checked={selectedOption === option}
                        onChange={handleOptionChange}
                      />
                      {option}
                    </div>
                  )
                )}
              </div>
              <div className="button-container">
                <button onClick={handleNextQuestion}>Siguiente Pregunta</button>
                <button onClick={handleResetQuiz}>Reiniciar Quiz</button>
              </div>
            </div>
          )
        )}
      </div>
    </div>
  );
};

export default App;
